def create():
    # Create a list of all bidding zones
    bidding_zones = [f"{area.name} = Area_details(name='{area.name}',code='{area.code}',tz='{area.tz}')" for area in Area]

    bidding_zones.sort()
    # Print all bidding zones
    for zone in bidding_zones:
        print(zone)

create()